mapper.select:
select a.SerialNo, a.LinkmanName, a.Phone, a.StartStation, (select c.RegionName from ecs_dic_region c where a.StartCityId = c.RegionId) StartCity, (select c.RegionName from ecs_dic_region c where a.EndCityId = c.RegionId) EndCity, a.Destination, a.StartTime, a.Remark from ecu_trip_customer a where a.IsEffective= '1'
and a.StartCityId=#{pd.startCity}
and a.EndCityId=#{pd.endCity}
and a.StartStation=#{pd.startStation}
and date_format(StartTime,'%Y-%m-%d')=#{pd.startDate}
order by a.PublishTime desc limit #{pd.currentPage},#{pd.showCount}

select userId, ifnull(Openid,'') openid, ifnull(unionId,'') unionid, ifnull(Nickname,'') nickName, Sex gender, Headimgurl avatarUrl, CityId city, ProvinceId province, CountryId country from ecu_user where 1=1
and unionId=#{unionid}
and userId=#{userId}
and Openid=#{openid}

select
a.UserId,a.Phone
from
ecu_blacklist a
where a.UserId = #{UserId}

    or a.Phone = #{Phone}

mapper.update:
update
ecu_mid_trip_user
set IsEffective = #{IsEffective}
where TripSerialNo=#{SerialNo} and UserId=#{UserId}
